    /*
     * Get sub_aspek by id_sub_aspek
     */
    function get_sub_aspek($id_sub_aspek)
    {
        return $this->db->get_where('tbl_sub_aspek',array('id_sub_aspek'=>$id_sub_aspek))->row_array();
    }
    
    /*
     * Get all sub_aspek
     */
    function get_all_sub_aspek()
    {
        $this->db->select('tbl_sub_aspek.*, tbl_aspek.nama_aspek, tbl_sub_praktikum.id_sub_praktikum, tbl_sub_praktikum.nama_sub_praktikum, tbl_praktikum.id_praktikum, tbl_praktikum.nama_praktikum');
		$this->db->from('tbl_sub_aspek');
		$this->db->join('tbl_aspek', 'tbl_aspek.id_aspek = tbl_sub_aspek.kd_aspek');
		$this->db->join('tbl_sub_praktikum', 'tbl_aspek.kd_sub_praktikum = tbl_sub_praktikum.id_sub_praktikum');
		$this->db->join('tbl_praktikum', 'tbl_praktikum.id_praktikum = tbl_sub_praktikum.kd_praktikum');
        return $this->db->get()->result_array();
    }
	
