    /*
     * function to add new sub_aspek
     */
    function add_sub_aspek($params)
    {
        $this->db->insert('tbl_sub_aspek',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update sub_aspek
     */
    function update_sub_aspek($id_sub_aspek,$params)
    {
        $this->db->where('id_sub_aspek',$id_sub_aspek);
        $response = $this->db->update('tbl_sub_aspek',$params);
        if($response)
        {
            return "sub_aspek updated successfully";
        }
        else
        {
            return "Error occuring while updating sub_aspek";
        }
    }
    
    /*
     * function to delete sub_aspek
     */
    function delete_sub_aspek($id_sub_aspek)
    {
        $response = $this->db->delete('tbl_sub_aspek',array('id_sub_aspek'=>$id_sub_aspek));
        if($response)
        {
            return "sub_aspek deleted successfully";
        }
        else
        {
            return "Error occuring while deleting sub_aspek";
